private void jbtnIngresoActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jbtnIngresoActionPerformed
// TODO add your handling code here:
    
    String  usrEmail, usrPass;
    int     usrNivel, usrActivo;
    
    if((jtxfUsuario.getText().length() > 0) && (jpfPass.getPassword().length > 0)) {
        try {

            Class.forName("org.gjt.mm.mysql.Driver");
            java.sql.Connection cnx = java.sql.DriverManager.getConnection("jdbc:mysql://localhost/TreintaYPlaya", "root", "");

            java.sql.PreparedStatement pstm = cnx.prepareStatement("select * from UsuariosWeb where usrEmail = ?") ;

            pstm.setString(1, jtxfUsuario.getText());
            
            java.sql.ResultSet rst = pstm.executeQuery();

            rst.next();
            usrEmail    = rst.getString("usrEmail");
            usrPass     = rst.getString("usrPass");
            usrNivel    = rst.getInt("usrNivel");
            usrActivo   = rst.getInt("usrActivo");
            
            char [] charPass = usrPass.toCharArray();
                        
            if(Arrays.equals(jpfPass.getPassword(), charPass)) {
                java.awt.EventQueue.invokeLater(new Runnable() {

                        @Override
                    public void run() {
                        new Reservas().setVisible(true);
                    }
                });
            
            } else {
                javax.swing.JOptionPane.showMessageDialog(this, "Contrase침a Incorrecta.", "Error", javax.swing.JOptionPane.ERROR_MESSAGE);
            }
            
            cnx.close();
            
        } catch (ClassNotFoundException cnfe) {
            javax.swing.JOptionPane.showMessageDialog(this, cnfe.getMessage(), "Error", javax.swing.JOptionPane.ERROR_MESSAGE);

        } catch (java.sql.SQLException sqle) {
            javax.swing.JOptionPane.showMessageDialog(this, sqle.getMessage(), "Error", javax.swing.JOptionPane.ERROR_MESSAGE);
        }
    }    
}//GEN-LAST:event_jbtnIngresoActionPerformed

