    private void jbtnAceptarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jbtnAceptarActionPerformed
        boolean valEmail, valPass;

        valEmail = valPass = false;

        // Valida Dirección de E-Mail

        if(!validaEmail(jtxfUsuario.getText())) {
            javax.swing.JOptionPane.showMessageDialog(this,
                                                      "Ingresó una Dirección de Correo Electrónico NO Válido.",
                                                      "Error",
                                                      javax.swing.JOptionPane.ERROR_MESSAGE);

            jtxfUsuario.setText("");
            jtxfUsuario.requestFocus();
        } else {
            valEmail = true;
        }


        // Valida Pass

        if(!validaPass(new String(jpfPass.getPassword()), new String(jpfValPass.getPassword()))) {
            javax.swing.JOptionPane.showMessageDialog(this,
                                                      "Las Contraseñas NO Coinciden.",
                                                      "Error",
                                                      javax.swing.JOptionPane.ERROR_MESSAGE);
            jpfPass.setText("");
            jpfValPass.setText("");
            jpfPass.requestFocus();
        } else {
            valPass = true;
        }

        // Agregar el Usuario a la Base
        if(valEmail && valPass) {
            try {

                Class.forName("org.gjt.mm.mysql.Driver");
                java.sql.Connection cnx = java.sql.DriverManager.getConnection("jdbc:mysql://localhost/TreintaYPlaya", "root", "");

                java.sql.PreparedStatement pstm = cnx.prepareStatement("insert into UsuariosWeb (usrEmail, usrPass, usrNivel, usrActivo) values (?,?,?,?)") ;

                pstm.setString(1, jtxfUsuario.getText());
                pstm.setString(2, String.valueOf(jpfPass.getPassword())/*toString()*/);
                pstm.setInt(3, 1);
                pstm.setInt(4, 1);

                int resultado = pstm.executeUpdate();

                javax.swing.JOptionPane.showMessageDialog(this, "El Usuario Se Registró Correctamente.", "Registro", javax.swing.JOptionPane.INFORMATION_MESSAGE);

                cnx.close();

            } catch (ClassNotFoundException cnfe) {
                javax.swing.JOptionPane.showMessageDialog(this, cnfe.getMessage(), "Error", javax.swing.JOptionPane.ERROR_MESSAGE);

            } catch (java.sql.SQLException sqle) {
                javax.swing.JOptionPane.showMessageDialog(this, sqle.getMessage(), "Error", javax.swing.JOptionPane.ERROR_MESSAGE);
            }

            dispose();
        }
    }//GEN-LAST:event_jbtnAceptarActionPerformed

    private boolean validaEmail(String email) {
        boolean resultado = false;
        int contAT = 0;
        int posAT = 0;
        int contPunto = 0;
        
        for(int i = 0; i < email.length(); i++)
            if(email.charAt(i) == '@') {
                contAT++;
                posAT = i;
            }
        
        for(int i = posAT; i < email.length(); i++)    
            if(email.charAt(i) == '.')
                contPunto++;
        
        
        if(contAT == 1 && contPunto >= 1)
            resultado = true;
        
        return resultado;
    }
    
    private boolean validaPass(String pass, String passVal) {
        boolean resultado = false;
        
        if(pass.equals(passVal))
            resultado = true;
        
        return resultado;
    }
    
    
